mapper.select:
SELECT * FROM emp e LEFT JOIN branch b on e.b_idx = b.b_idx
AND b.b_name = #{opt}
AND is_delete = 0

SELECT * FROM emp e
LEFT JOIN wphoto w ON w.board_num = e.emp_no
WHERE emp_no = #{param1}

mapper.update:
UPDATE emp SET 
	  pw = #{pw}
	, name = #{name}
	, gender = #{gender}
	, birth = #{birth}
	, phone = #{phone}
	, email = #{email}
	, position = #{position}
	, status = #{status}

	, retire_year = #{retire_year}


WHERE emp_no = #{emp_no}

mapper.delete:
UPDATE emp
	SET is_delete = 1
WHERE emp_no = #{param1}

