mapper.update:
UPDATE emp SET 
	  pw = #{pw}
	, name = #{name}
	, gender = #{gender}
	, birth = #{birth}
	, phone = #{phone}
	, email = #{email}
	, position = #{position}
	, start_conterm = #{start_conterm}
	, end_conterm = #{end_conterm}
	, status = #{status}
	, join_year = #{join_year}
WHERE emp_no = #{emp_no}

mapper.delete:
UPDATE emp
	SET is_delete = 1
WHERE emp_no = #{param1}
